    Tamanios Tamanio = new Tamanios();
    [Parameter] public int idTamanio { get; set; }

    protected async override Task OnParametersSetAsync()
    {
        Tamanio = await Http.GetFromJsonAsync<Tamanios>($"/api/tamanios/{idTamanio}");
    }

    void Volver()
    {
        NavigationManager.NavigateTo("/lista-tamanio");
    }

    async Task EditarTamanios()
    {
        var confirmacion = await JS.InvokeAsync<bool>("confirmar", "Editar",
            $"¿Desea editar {Tamanio.Nombre}?", "question");
        if (confirmacion)
        {
            var respuesta = await Http.PutAsJsonAsync("/api/tamanios", Tamanio);

            if (respuesta.IsSuccessStatusCode)
            {
                NavigationManager.NavigateTo("/lista-tamanios");
                //await JS.InvokeVoidAsync("simple", "¡Éxito!", "Marca editada", "success");
                Snackbar.Add("Editado con éxito", Severity.Success,
                a => a.VisibleStateDuration = 200);
            }
            else
            {
                //await JS.InvokeVoidAsync("simple", "Error",
                //    "No se pudo editar la marca", "error");
                Snackbar.Add("Ha ocurrido un error", Severity.Error);
            }
        }
    }

    async Task BorrarTamanio(int idCategoria)
    {
        var confirmacion = await JS.InvokeAsync<bool>("confirmar", "Eliminar",
            $"¿Desea eliminar {Tamanio.Nombre}?", "warning");
        if (confirmacion)
        {
            var respuesta = await Http.DeleteAsync($"/api/tamanios/{Tamanio.Id}");

            if (respuesta.IsSuccessStatusCode)
            {
                NavigationManager.NavigateTo("/lista-tamanios");
                await JS.InvokeVoidAsync("simple", "¡Éxito!", "Tamaño eliminada", "success");
            }
            else
            {
                await JS.InvokeVoidAsync("simple", "Error",
                    "No se pudo eliminar", "error");
            }
        }
    }

